item.solution:
#include<stdio.h>
#define PI 3.14159265358979323846

int main() {
    double r = 3.1;
    double circumference = 2 * PI * r;
    double area = PI * r * r;
    printf("%.2f\n%.2f\n", circumference, area);
    return 0;
}

#include<stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    if (n % 2 == 0)
        printf("偶数\n");
    else
        printf("奇数\n");
    return 0;
}
